(66,	'wc-payments-notes-set-https-for-checkout',	'info',	'en_US',	'Enable secure checkout',	'Enable HTTPS on your checkout pages to display all available payment methods and protect your customers data.',	'{}',	'unactioned',	'woocommerce-payments',	'2023-07-17 15:25:09',	NULL,	0,	'plain',	'',	0,	0,	'info'),
(67,	'wc-admin-choosing-a-theme',	'marketing',	'en_US',	'Choosing a theme?',	'Check out the themes that are compatible with WooCommerce and choose one aligned with your brand and business needs.',	'{}',	'unactioned',	'woocommerce-admin',	'2023-07-18 14:49:25',	NULL,	0,	'plain',	'',	0,	0,	'info'),
(68,	'wc-admin-add-first-product-note',	'email',	'en_US',	'Add your first product',	'{greetings}<br /><br />Nice one; you\'ve created a WooCommerce store! Now it\'s time to add your first product and get ready to start selling.<br /><br />There are three ways to add your products: you can <strong>create products manually, import them at once via CSV file</strong>, or <strong>migrate them from another service</strong>.<br /><br /><a href=\"https://woocommerce.com/document/managing-products/?utm_source=help_panel&amp;utm_medium=product\">Explore our docs</a> for more information, or just get started!',	'{\"role\":\"administrator\"}',	'unactioned',	'woocommerce-admin',	'2023-07-19 15:29:00',	NULL,	0,	'plain',	'http://the-book-marketplace.local/wp-content/plugins/woocommerce/images/admin_notes/dashboard-widget-setup.png',	0,	0,	'info'),
(69,	'wc-admin-mobile-app',	'info',	'en_US',	'Install Woo mobile app',	'Install the WooCommerce mobile app to manage orders, receive sales notifications, and view key metrics  wherever you are.',	'{}',	'unactioned',	'woocommerce-admin',	'2023-07-19 15:29:00',	NULL,	0,	'plain',	'',	0,	0,	'info'),
(70,	'square_payments_q3_2023',	'marketing',	'en_US',	'Square for WooCommerce',	'Connect your Square account with WooCommerce to accept online and in-person payments (including contactless payments and cross-channel gift cards) quickly and securely. Save time by syncing sales, customers, and items and inventory  and manage everything through one centralized platform.',	'{}',	'pending',	'woocommerce.com',	'2023-07-19 15:29:09',	NULL,	0,	'plain',	'',	0,	0,	'info'),
(71,	'wc-admin-launch-checklist',	'info',	'en_US',	'Ready to launch your store?',	'To make sure you never get that sinking \"what did I forget\" feeling, we\'ve put together the essential pre-launch checklist.',	'{}',	'unactioned',	'woocommerce-admin',	'2023-07-20 14:26:27',	NULL,	0,	'plain',	'',	0,	0,	'info'),
(72,	'wc-admin-marketing-jetpack-backup',	'marketing',	'en_US',	'Protect your WooCommerce Store with Jetpack Backup.',	'Store downtime means lost sales. One-click restores get you back online quickly if something goes wrong.',	'{}',	'unactioned',	'woocommerce-admin-notes',	'2023-07-20 14:26:27',	NULL,	0,	'thumbnail',	'http://the-book-marketplace.local/wp-content/plugins/woocommerce/assets/images/admin_notes/marketing-jetpack-2x.png',	0,	0,	'info'),
(73,	'wc-admin-onboarding-payments-reminder',	'info',	'en_US',	'Start accepting payments on your store!',	'Take payments with the provider thats right for you - choose from 100+ payment gateways for WooCommerce.',	'{}',	'unactioned',	'woocommerce-admin',	'2023-07-22 19:57:51',	NULL,	0,	'plain',	'',	0,	0,	'info'),
(74,	'wc-admin-customizing-product-catalog',	'info',	'en_US',	'How to customize your product catalog',	'You want your product catalog and images to look great and align with your brand. This guide will give you all the tips you need to get your products looking great in your store.',	'{}',	'unactioned',	'woocommerce-admin',	'2023-07-23 19:26:44',	NULL,	0,	'plain',	'',	0,	0,	'info'),
(75,	'wc-admin-usage-tracking-opt-in',	'info',	'en_US',	'Help WooCommerce improve with usage tracking',	'Gathering usage data allows us to improve WooCommerce. Your store will be considered as we evaluate new features, judge the quality of an update, or determine if an improvement makes sense. You can always visit the <a href=\"http://the-book-marketplace.local/wp-admin/admin.php?page=wc-settings&#038;tab=advanced&#038;section=woocommerce_com\" target=\"_blank\">Settings</a> and choose to stop sharing data. <a href=\"https://woocommerce.com/usage-tracking?utm_medium=product\" target=\"_blank\">Read more</a> about what data we collect.',	'{}',	'unactioned',	'woocommerce-admin',	'2023-07-24 14:25:59',	NULL,	0,	'plain',	'',	0,	0,	'info'),
(76,	'wc-admin-woocommerce-payments',	'marketing',	'en_US',	'Try the new way to get paid',	'Securely accept credit and debit cards on your site. Manage transactions without leaving your WordPress dashboard. Only with <strong>WooCommerce Payments</strong>.<br><br>By clicking \"Get started\", you agree to our <a href=\"https://wordpress.com/tos/\" target=\"_blank\">Terms of Service</a>',	'{}',	'actioned',	'woocommerce-admin',	'2023-07-24 14:25:59',	NULL,	0,	'plain',	'',	0,	0,	'info');
